      time: '2h ago',
      unread: 0,
      online: false
    },
    {
      id: 5,
      name: 'David Kim',
      avatar: 'üë®‚Äçüî¨',
      lastMessage: 'The research findings are fascinating!',
      time: '1d ago',
      unread: 0,
      online: true
    }
  ])

  const [selectedChat, setSelectedChat] = useState(conversations[0])
  const [newMessage, setNewMessage] = useState('')
  const [messages, setMessages] = useState([
    {
      id: 1,
      sender: 'Sarah Chen',
      content: 'Hey! How are you doing?',
      time: '10:30 AM',
      isOwn: false
    },
    {
      id: 2,
      sender: 'You',
      content: 'I\'m doing great! Just finished working on my portfolio.',
      time: '10:32 AM',
      isOwn: true
    },
    {
      id: 3,
      sender: 'Sarah Chen',
      content: 'That\'s awesome! I\'d love to take a look at it.',
      time: '10:33 AM',
      isOwn: false
    },
    {
      id: 4,
      sender: 'You',
      content: 'Sure! Here\'s the link: www.myportfolio.com',
      time: '10:35 AM',
      isOwn: true
    },
    {
      id: 5,
      sender: 'Sarah Chen',
      content: 'Thanks for sharing your portfolio! It looks amazing.',
      time: '10:36 AM',
      isOwn: false
    }
  ])

  const handleSendMessage = () => {
    if (newMessage.trim()) {
      const message = {
        id: messages.length + 1,
        sender: 'You',
        content: newMessage,
        time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
        isOwn: true
      }
      setMessages([...messages, message])
      setNewMessage('')
    }
